import {
	Heading,
	Avatar,
	Box,
	Center,
	Text,
	Stack,
	Button,
	Badge,
	useColorModeValue,
} from "@chakra-ui/react";
import { useContext, useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { AppContext } from "../App";
import axios from "axios";

export default function Profile() {
	const navigate = useNavigate();
	const [userinfo, setUserInfo] = useState({});
	const { setAuth, setVerify, setId, id } = useContext(AppContext);
	const signOut = () => {
		setAuth(false);
		setVerify(false);
		setId("");
		navigate("/");
	};
	const GET_URL = `http://localhost:3001/api/student/user/login/${id}`;
	const getUser = () => {
		axios.get(GET_URL).then((res) => setUserInfo(res.data));
	};
	useEffect(() => {
		getUser();
	}, []);
	return (
		<Center py={6}>
			<Box
				maxW={"320px"}
				w={"full"}
				bg={useColorModeValue("white", "gray.900")}
				boxShadow={"2xl"}
				rounded={"lg"}
				p={6}
				textAlign={"center"}>
				<Avatar
					size={"xl"}
					src={
						"data:image/png;base64,"
					}
					alt={"Avatar Alt"}
					mb={4}
					pos={"relative"}
					_after={{
						content: '""',
						w: 4,
						h: 4,
						bg: "green.300",
						border: "2px solid white",
						rounded: "full",
						pos: "absolute",
						bottom: 0,
						right: 3,
					}}
				/>
				<Heading fontSize={"2xl"} fontFamily={"body"}>
					{userinfo.username}
				</Heading>
				<Text fontWeight={600} color={"gray.500"} mb={4}>
					{userinfo.email}
				</Text>
				<Text
					textAlign={"center"}
					color={useColorModeValue("gray.700", "gray.400")}
					px={3}>
					Welcome to Insight Club!
				</Text>

				<Stack align={"center"} justify={"center"} direction={"row"} mt={6}>
					<Badge
						px={2}
						py={1}
						bg={useColorModeValue("green.200", "green.800")}
						fontWeight={"400"}>
						Email Verified
					</Badge>
				</Stack>

				<Stack mt={8} direction={"row"} spacing={4}>
					<Button
						flex={1}
						fontSize={"sm"}
						rounded={"full"}
						onClick={signOut}
						_focus={{
							bg: "gray.200",
						}}>
						Sign out
					</Button>
				</Stack>
			</Box>
		</Center>
	);
}
